function showNoty(y,t){var b,n;switch(y){case 'danger':b='bg-danger';break;case 'warning':b='bg-warning';break;case 'success':b='bg-success';break;default:b='bg-info';break;}n=noty({text:t,type:y,theme:'defaultTheme',template:'<div class="noty_message '+b+'"><span class="glyphicon glyphicon-warning-sign" style="color:white" aria-hidden="true"></span><b><span class="noty_text"></span><div class="noty_close"></b></div></div>',animation:{open:'animated pulse',close:'animated bounceOutLeft',easing:'swing',speed:500},timeout:3000,});}
function toasty(t,s){s=s.toLowerCase();var b,c;switch(s){case 'error':b='#f44336';c='black';break;case 'success':b='green';c='white';break;default:b='black';c='white';break;}var $tC=$(`<span>${t}</span>`);Materialize.toast($tC,1000);$('.toast').css('background-color',b);$('.toast').css('color',c);$('#toast-container').css('top','auto');$('#toast-container').css('right','auto');$('#toast-container').css('bottom','50%');}
var w = window;
var st=w.localStorage;var eL=['index','registro','restaurarPass'];
function getLoc(){return w.location.pathname.split('/')[(w.location.pathname.split('/')).length-1].split('.')[0];}
function checkId(){if(eL.indexOf(getLoc())==-1){if(getItem('id')==undefined){logout();}}}
checkId();
function checkAdministrador(){if(getItem('administrador')=='t'){return true;}else{return false;}}
function setCantidad(){var c=getItem('cantidad');if(parseInt(c)>0){$('.cantGraduados').html(c);}else{$('.cantGraduados').hide();}}
function initializeCantidad(c){setItem('cantidad',c);setCantidad();}
function getItem(i){return st.getItem(i);}
function removeItem(i){st.removeItem(i);}
function setItem(i,v){st.setItem(i,v);}
function logout(){removeItem('id');removeItem('administrador');removeItem('cantidad');setItem('logout','t');w.location.href='index.html';}
function checkMenu(){if(eL.indexOf(getLoc())!=-1){return};var cH,pH,eH,dH;if(checkAdministrador()){cH='cursosAdmin.html';pH='posgradosAdmin.html';eH='empleoAdmin.html';dH='descuentosAdmin.html';$('.hideNotAdmin').show();successGetCantGraduados();}else{cH='cursos.html';pH='posgrados.html';eH='empleo.html';dH='descuentos.html';$('.hideNotAdmin').hide();}$('.cursosHref').prop('href',cH);$('.posgradosHref').prop('href',pH);$('.empleoHref').prop('href',eH);$('.descuentosHref').prop('href',dH);}
function successGetCantGraduados(){var c=getItem('cantidad');if(c==null){return};if(parseInt(c)>0){$('.cantGraduados').show();$('.cantGraduados').html(c);}else{$('.cantGraduados').hide();}}
function controlVacio(n){if($.trim($(n).val())==''){$(n).css('box-shadow','0px 0px 1px 1px #f24d4d');$(n).focus();return false;}return true;}
function sacarColor(m){$(m).css('box-shadow','0px 0px 0px 0px #ccc');}
function getCard(cT,cC){return `<div class="row"><div class="col s12"><div class="card blue-grey darken-1"><div class="card-content white-text"><span class="card-title">${cT}</span><p class="center-align">${cC}</p></div></div></div></div>`;}
function controlBack(){w.onload=function (){var l=getLoc();if (typeof history.pushState==="function"){history.pushState("jibberish", null, null);w.onpopstate=function (){if(l=='inicio'||l=='index'){history.pushState('newjibberish', null, null);navigator.app.exitApp();}else{w.location.href="inicio.html";}};}else {var ignoreHashChange=true;w.onhashchange=function (){if(l=='inicio'||l=='index'){history.pushState('newjibberish', null, null);navigator.app.exitApp();}else{w.location.href="inicio.html";}};}}}
function parse(j){return JSON.parse(j);}
function checkSelectedOption(){var o,l,n;o={'cursos':'liCurso','cursosAdmin':'liCurso','descuentos':'liDescuento','descuentosAdmin':'liDescuento','empleo':'liEmpleo','empleoAdmin':'liEmpleo','graduadosAdmin':'liGraduado','perfil':'liPerfil','posgrados':'liPosgrado','posgradosAdmin':'liPosgrado'};l=getLoc();n=`.${o[l]}`;$(n).addClass('selectedOption');}
document.addEventListener('deviceready', function() {alert('DeviceReady');var exitApp=true, intval=setInterval(function (){exitApp=true;}, 1000);document.addEventListener("backbutton", function (e){e.preventDefault();if (exitApp) {clearInterval(intval);(navigator.app && navigator.app.exitApp()) || (device && device.exitApp())}else{exitApp=true;history.back(1);}}, false);}, false);
